import { types } from "./types"
function preloaderOn (){
    return{
        type: types.PRELOADER_ON 
    }
}
function preloaderOff (){
    return{
        type: types.PRELOADER_OFF 
    }
}

export function addUserAction (user) {
    return async function (dispatch) {
        dispatch(preloaderOn())
        const options = {
            method: "POST",
            headeres: {
                "content-Type": 'application/json'
            },
            body: JSON.stringify(user)
        }
        const response = await fetch('https://jsonplaceholder.typicode.com/', options)
        if(response.status===200 || response.status===404){
            dispatch(preloaderOff())
        }
    }
}
import { FETCH_USERS_REQUEST, FETCH_USERS_SUCCESS, FETCH_USERS_FAILURE } from './userActionTypes';

export const fetchUsersRequest = () => ({
  type: FETCH_USERS_REQUEST,
});

export const fetchUsersSuccess = (users) => ({
  type: FETCH_USERS_SUCCESS,
  payload: users,
});

export const fetchUsersFailure = (error) => ({
  type: FETCH_USERS_FAILURE,
  payload: error,
});
